  IF ( ALLOCATED(InData%i3) ) THEN
    Int_BufSz   = Int_BufSz   + 2*2  ! i3 upper/lower bounds for each dimension
      Db_BufSz   = Db_BufSz   + SIZE(InData%i3)  ! i3
  END IF
