IF (ALLOCATED(SrcMiscData%AugMat_pivot)) THEN
  i1_l = LBOUND(SrcMiscData%AugMat_pivot,1)
  i1_u = UBOUND(SrcMiscData%AugMat_pivot,1)
  IF (.NOT. ALLOCATED(DstMiscData%AugMat_pivot)) THEN 
