  y_out%TwrAccel = y1%TwrAccel + b0 * t_out + c0 * t_out**2
  b0 = (t(3)**2*(y1%YawAngle - y2%YawAngle) + t(2)**2*(-y1%YawAngle + y3%YawAngle))/(t(2)*t(3)*(t(2) - t(3)))
