 SUBROUTINE FAST_DestroyServoDyn_Data( ServoDyn_DataData, ErrStat, ErrMsg )
  TYPE(ServoDyn_Data), INTENT(INOUT) :: ServoDyn_DataData
  INTEGER(IntKi),  INTENT(  OUT) :: ErrStat
