      Int_BufSz   = Int_BufSz + 3  ! m: size of buffers for each call to pack subtype
      CALL HydroDyn_PackMisc( Re_Buf, Db_Buf, Int_Buf, InData%m, ErrStat2, ErrMsg2, .TRUE. ) ! m 
