  CALL FEAM_DestroyOtherState( FEAMooring_DataData%OtherSt(i1), ErrStat, ErrMsg )
ENDDO
  CALL FEAM_DestroyParam( FEAMooring_DataData%p, ErrStat, ErrMsg )
  CALL FEAM_DestroyInput( FEAMooring_DataData%u, ErrStat, ErrMsg )
